elseif solve == enumSolve.Social then
	tbTable:PrintSolution(cRegion, WorldLua, "Social");
	WorldLua:AddMsg(XT("{0}尝试安抚他们，展现了处世之道：“那好啊，你们叫它的名字，看它答应谁？”果然，其中一人开始支支吾吾了。群众投来赞许的目光。"), pmName);
	tbTable:AddInfluence(WorldLua, cRegion);
	tbTable:SlightlyIncreasePop(WorldLua, cRegion);
elseif solve == enumSolve.Fight then
	tbTable:PrintSolution(cRegion, WorldLua, "Fight");
	if unionData.Fight > tbTable:SPL(1) then
		WorldLua:AddMsg(XT("{0}朝他们喊到：“区区一条狗有什么好争的？我拿走了！”威慑力十足，其中一人被吓得直接逃跑。另一个人开始也很慌张，但还是说狗是她的。{0}将狗还给了她。群众感到此处更加安定。"), pmName);
		tbTable:SlightlyIncreaseStab(WorldLua, cRegion);
		tbTable:SlightlyIncreasePop(WorldLua, cRegion);
	else
		WorldLua:AddMsg(XT("{0}朝他们喊到：“区区一条狗有什么好争的？”并摆出吓人的架势。“你是哪里来的，这狗可是我当年的嫁妆！”其中一个人说。另一个人说：“胡说，它明明就是我4岁开始养的。”争吵继续进行，依靠{0}现在的武力无法威慑住他们。"), pmName);
		tbTable:NoEvent(WorldLua, cRegion);
	end
elseif solve == enumSolve.Charm then
	tbTable:PrintSolution(cRegion, WorldLua, "Charm");
	WorldLua:AddMsg(XT("{0}对众人说：“你们都来做我们的信徒，我送你们一人条狗！”但他们并不相信，只想争夺眼前这条狗。"), pmName);
	tbTable:NoEvent(WorldLua, cRegion);
elseif solve == enumSolve.Intelligence then
	tbTable:PrintSolution(cRegion, WorldLua, "Intelligence");
	WorldLua:AddMsg(XT("{0}说：“这样，我把这条狗拿回去养，生了小狗给你们一人一条怎么样？”他们并不相信，只想争夺眼前这条狗。"), pmName);
	tbTable:NoEvent(WorldLua, cRegion);
elseif solve == enumSolve.Food then
	tbTable:PrintSolution(cRegion, WorldLua, "Food");
	WorldLua:AddMsg(XT("{0}带了些食物，喂给了狗。他们双方愣住了，不知道{0}在干嘛。"), pmName);
	tbTable:NoEvent(WorldLua, cRegion);
elseif solve == enumSolve.LingStone then
	tbTable:PrintSolution(cRegion, WorldLua, "LingStone");
	WorldLua:AddMsg(XT("{0}带了些灵石，但附近无人认识灵石。"), pmName);
	tbTable:NoEvent(WorldLua, cRegion);
elseif solve == enumSolve.Wood then
	tbTable:PrintSolution(cRegion, WorldLua, "Wood");
	WorldLua:AddMsg(XT("{0}拿了根木棍，想要给他们两人一人一棒。他们被吓跑了。"), pmName);
	tbTable:NoEvent(WorldLua, cRegion);
elseif solve == enumSolve.Rock then
	tbTable:PrintSolution(cRegion, WorldLua, "Rock");
	WorldLua:AddMsg(XT("{0}拿了块石头，将狗一砖打死，说：“狗肉一人一半。”他们十分害怕，再也不敢闹事了。但民众有点害怕。"), pmName);
