local tbTable = GameMain:GetMod("MapStoryHelper");
local cRegion = story.target.region;
local School1 = story.target.schools[0];
local unionData = cRegion.UnionData;
local solve = cRegion.RegionPolicy.Way;
local pmName = unionData:GetPMName();
local enumSolve = CS.XiaWorld.OutspreadMgr.Region.SolveWay;
tbTable:DefuseSubmiss(WorldLua, cRegion);
if not solve or solve == enumSolve.None then
	tbTable:PrintSolution(cRegion, WorldLua, "None");
	WorldLua:AddMsg(XT("受戒顺利进行。"));
	tbTable:IncreaseStab(WorldLua, cRegion);
	tbTable:SlightlyIncreaseOtherPop(WorldLua, cRegion, School1);
elseif solve == enumSolve.Social then
	tbTable:PrintSolution(cRegion, WorldLua, "None");
	WorldLua:AddMsg(XT("{0}走上前去，说道：“依我看这世道，要不还是算了吧。”对方有点恼火，说：“你是哪个派的？管得着吗？”{0}无奈只能离去。受戒顺利进行。"), pmName);
	tbTable:IncreaseOtherAttra(WorldLua, cRegion, School1);
	tbTable:SlightlyIncreaseOtherPop(WorldLua, cRegion, School1);
elseif solve == enumSolve.Fight then
	tbTable:PrintSolution(cRegion, WorldLua, "Fight");
	WorldLua:AddMsg(XT("{0}直接走上去，与对方打了起来，但被信众阻止了。群众用异样的眼神看着{0}。受戒中断了一会又顺利进行。"), pmName);
	tbTable:IncreaseStab(WorldLua, cRegion);
	tbTable:SlightlyIncreaseOtherPop(WorldLua, cRegion, School1);
	tbTable:SlightlyDecreaseAttra(WorldLua, cRegion);
elseif solve == enumSolve.Charm then
	tbTable:PrintSolution(cRegion, WorldLua, "Charm");
	WorldLua:AddMsg(XT("{0}当着他们的面，开始大讲道法，想要吸引信众。但受戒仪式非常严肃，没有办法依靠传道进行干扰。受戒顺利进行。"), pmName);
	tbTable:IncreaseStab(WorldLua, cRegion);
	tbTable:SlightlyIncreaseOtherAttra(WorldLua, cRegion, School1);
elseif solve == enumSolve.Intelligence then
	tbTable:PrintSolution(cRegion, WorldLua, "Intelligence");
	if unionData.Intelligence > tbTable:MPL(1) then
		WorldLua:AddMsg(XT("{0}走上前去，决定就受戒的后果与他们辩论一番。对方竟然被说得哑口无言，受戒无法再进行下去。"), pmName);
		tbTable:SlightlyDecreaseOtherAttra(WorldLua, cRegion, School1);
		tbTable:SlightlyDecreaseOtherPop(WorldLua, cRegion, School1);
	else
		WorldLua:AddMsg(XT("{0}走上前去，决定就受戒的后果与他们辩论一番。然而由于{0}知识尚浅，三言两语就被驳了回来。受戒顺利进行。"), pmName);
		tbTable:IncreaseStab(WorldLua, cRegion);
		tbTable:SlightlyIncreaseOtherAttra(WorldLua, cRegion, School1);
	end
elseif solve == enumSolve.Food then
	tbTable:PrintSolution(cRegion, WorldLua, "Food");
	WorldLua:AddMsg(XT("{0}拿了一些食物，在他们面前吃了起来。他们的代理人看的口水直流，想必已经几天没吃过肉了。他们的信徒非常怀疑，纷纷离去。"), pmName);
	tbTable:SlightlyDecreaseOtherAttra(WorldLua, cRegion, School1);
	tbTable:DecreaseOtherPop(WorldLua, cRegion, School1);
elseif solve == enumSolve.LingStone then
	tbTable:PrintSolution(cRegion, WorldLua, "LingStone");
	if CS.XiaWorld.OutspreadMgr.Instance:CanCostItem("Item_LingStone", 100) then
		WorldLua:AddMsg(XT("{0}与对方私下沟通，小声说道：“今天要么就先这样吧，下次再受戒怎么样？”并将灵石塞到他们{0}手中。“长久只吃素对身体不好，有时候不必勉强自己，只要心中有数即可。”对方突然这么说，并离开了。受戒中断了。"), pmName);
		CS.XiaWorld.OutspreadMgr.Instance:CostItem(cRegion, "Item_LingStone", 100, nil);
		tbTable:SlightlyDecreaseOtherPop(WorldLua, cRegion, School1);
	else
		WorldLua:AddMsg(XT("{0}摸了摸口袋，并与门派沟通，发现连100灵石都没有。"), pmName);
		tbTable:NoEvent(WorldLua, cRegion);
	end
elseif solve == enumSolve.Wood then
	tbTable:PrintSolution(cRegion, WorldLua, "Wood");
	WorldLua:AddMsg(XT("{0}拿了一根棍子，准备直接攻击对方。然而他们的信众太多，被阻止了。群众看见了之后对我们表示不满。受戒没有进行地很顺利。"), pmName);
	tbTable:SlightlyIncreaseStab(WorldLua, cRegion);
	tbTable:SlightlyIncreaseOtherAttra(WorldLua, cRegion, School1);
	tbTable:SlightlyDecreaseAttra(WorldLua, cRegion);
elseif solve == enumSolve.Rock then
	tbTable:PrintSolution(cRegion, WorldLua, "Rock");
	WorldLua:AddMsg(XT("{0}拿了一块石头向对方砸了过去，但被其接住。“你们看看，这就是他们的作风！”群众对我们表示怀疑，并心有不安。受戒没有进行地很顺利。"), pmName);
