		me:AddMsg(XT("[NAME]翻找尸体，找到了两本秘籍"));
	else
		me:AddMsg(XT("尸体上没有任何东西"));
	end
	tMap.bGet = true;
else
	me:AddMsg(XT("尸体上没有任何东西"));
end


local tMap = WorldLua:GetFightMapHelper();
if tMap:GetFlag(1) == 1 and tMap:GetFlag(2) == 1 and tMap:GetFlag(3) == 1 then
	me:AddMsg(XT("无形中有股力量包围着[NAME]，瞬息之间，[NAME]来到一个荒芜而孤寂的地方。"));
	me.npcObj:TeleportNextStep(15915);
else
	me:AddMsg(XT("无事发生。"));
end


