	GameUlt.MakeAuctionCanUse()
	me:AddMsg(XT("下次拍卖将在20天后举行。"));
	me:AddMsg(XT("获得了前往拍卖会的资格。"));
else
	me:AddMsg(XT("但你已获得拍卖会的资格。"));
	if GameUlt.GetNextAuctionDay() then
		me:AddMsg(XT("拍卖会预计在{0}天后开启。"), math.ceil(GameUlt.GetNextAuctionDay()));
	else
		me:AddMsg(XT("拍卖会正在举行，请派人前往。"));
	end
